#define RADIUS 0.3
#define ANGLE_NUM 5.0
#define INNER_RADIUS 1.1
#define ROT_SPD 2.2
#define PI 3.1415926535897932384626

float getStar(vec2 pos, vec2 uv)
{    
    float d =1.0 - distance(pos/iResolution.x, uv) / RADIUS;
    d = clamp(d, 0.0, 1.0);

    vec2 dir = normalize(uv - pos/iResolution.x);
    float angle = (atan(dir.y, dir.x) + PI) + iTime * ROT_SPD;
    angle *= ANGLE_NUM;

    float star = abs(fract(angle / PI / 2.0) * 2.0 - 1.0);
    star = 1.7 + pow(star, 2.3);

    float c = star * d;

    c = ceil(c - INNER_RADIUS);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;

    float c = getStar(iMouse.xy, uv);

    // Output to screen
    fragColor = vec4(c, c, c,1.0);
}
