#define PI 3.14159265359

float complex_magnitude2(vec2 complex)
{
	return complex.x * complex.x + complex.y * complex.y;
}

vec2 complex_mult(vec2 a, vec2 b)
{
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

vec2 complex_add(vec2 a, vec2 b)
{
    return vec2(a.x + b.x, a.y + b.y);
}

float julia(float x, float y) {
	int i = 0;
	int iterations = 200;

    vec2 a = vec2(x, y);
    vec2 c = vec2(-0.8, 0.17);
    c.y+=sin(iTime)*0.06+0.05;

	int cont = 0;
	for (i = 0; i < iterations; i++) {
		a = complex_add(complex_mult(a, a), c);
		if (complex_magnitude2(a) > 1000.0)
			return float(cont)/float(iterations);
		cont+=1;
	}
	return float(cont)/float(iterations);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 old_uv = fragCoord/iResolution.xy;

    // Normalized pixel coordinates (from -1 to 1 in x and y)
    float x = (-1.0 + (fragCoord.x / iResolution.x) * 2.0);
	float y = (-1.0 + (fragCoord.y / iResolution.y) * 2.0);
    vec2 uv = vec2(x, y);

    float aspect = iResolution.x/iResolution.y; 
    uv.x *= aspect;

    vec2 pos = uv * 0.9f;
    float intensity = 1.5;
    intensity+=sin(iTime)*1.0+1.5;


    float juliaValue = julia(pos.x, pos.y);
    vec3 final_color = vec3(0.2f, 0.9f, 0.2f) * juliaValue * intensity;

    // Output to screen
    fragColor = vec4(final_color,1.0);
}
