//#define FORCE_BUG
#define BUG 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float a = 0.0, b = 0.0;

#ifndef FORCE_BUG

#if BUG == 1
    a = uv.x > 0.5 ? b = uv.y > 0.5 ? 1.0 : 0.0, 1.0 : 0.0;
#elif BUG == 2
    b = uv.x > 0.5 ? a = 1.0, uv.y > 0.5 ? 1.0 : 0.0 : 0.0;
#else
    a = uv.x > 0.5 ? 1.0 : 0.0;
    b = uv.y > 0.5 && uv.x > 0.5 ? 1.0 : 0.0;
#endif

#else

#if BUG == 1
    float temp = (b = uv.y > 0.5 ? 1.0 : 0.0, 1.0);
    a = uv.x > 0.5 ? temp : 0.0;
#elif BUG == 2
    float temp = (a = 1.0, uv.y > 0.5 ? 1.0 : 0.0);
    b = uv.x > 0.5 ? temp : 0.0;
#endif

#endif

    fragColor = vec4(a, b, 0.0,1.0);
}
