#define M 100
#define B 10000.
#define S 3

vec2 c;

vec2 f(vec2 v) 
{
    return vec2(v.x/cos(v.y), v.y/sin(v.x))+c;
}

void mainImage( out vec4 kk, in vec2 cc)
{
    float t = iTime/5.;
    c = vec2(cos(t),sin(t));

    vec2 k = 6.283*cc/iResolution.xy-3.141;

    int i = 0;
    while (i < M)
    {
        k = f(k);
        if (length(k) > B) break;
        i++;
    }

    t = float(i*S)/float(M);
    kk = vec4(sqrt(t),t*t*t,max(0.,sin(6.283*t)),1.0);
}
