void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = fragCoord*((sin(iTime*0.17)+2.0)*0.5);
    float brightness = distance(fragCoord, iResolution.xy*0.5)/max(iResolution.x, iResolution.y);
    fragColor = vec4(brightness*(sin(iTime)+1.1), brightness*(sin(iTime*0.8)+1.1), brightness*(sin(iTime*1.1)+0.8), brightness);

    fragCoord = fragCoord*((sin(iTime*0.76)+2.0)*0.5);
    brightness = distance(fragCoord, iResolution.xy*0.5)/max(iResolution.x, iResolution.y);
    fragColor /= brightness;
}
