// -9  thanks to kishimisu
// -11 thanks to iq
// -3  thanks to Xor
void mainImage( out vec4 O, vec2 I )
{
    float b, i;

    for(vec2 u = I/iResolution.y + .1*iTime; 
        i++<10.; 
        u += .1+26.*floor(.7*iTime))
        b = .5*(b + fract(dot(atan(I=floor(9.*u)),I)));

    O = .7 * sqrt(1. - cos(19.*b) + cos(69.*(b-=b*b)*b + vec4(6,1,2,3)) );
}

