#define PI 3.1415926538
mat2 rotate(float angle) {
    return mat2( cos(angle), -sin(angle),
                 sin(angle), cos(angle));
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    float box = length(max(d,0.0)) + min(max(d.x,d.y),0.0);
    return 1.-step(0.005, abs(box));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*0.5) /iResolution.y;

    float spd = iTime*0.25;
    //uv *= rotate(PI*0.25);
    //uv *= rotate(spd);    
    vec3 col = vec3(0.0);
    col.rg = uv +.5;

    vec2 size = vec2(0.2); //*abs(sin(iTime));
    vec2 uvD = uv;//*rotate(spd);
    float d = sdBox(uvD, size);

    size += vec2(.1, .2);
    //size *= abs(vec2(sin(spd),cos(spd)));
    //size += 0.025;
    float e = sdBox(uv, size);
    float f = sdBox(uv, size.yx);

    col.r = step(0.005, abs(d));
    col.g = step(0.005, abs(e));
    col.b = step(0.005, abs(f));

    //col = vec3(col.r+col.g+col.b);
    //col = step(0.005, abs(col));
    col = 1.0-col;

    // Output to screen
    fragColor = vec4(col,1.0);
}
