const uint MAX_UINT = 0xffffffffu;
uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
uvec3 u = uvec3(1, 2, 3);

uvec3 h3(uvec3 n){
    n ^= (n.yzx << u);
    n ^= (n.yzx >> u);
    n *= k;
    n ^= (n.yzx << u);
    return n * k;
}

vec3 h33(vec3 p){
    uvec3 n = floatBitsToUint(p);
    return vec3(h3(n)) / vec3(MAX_UINT);
}

float h31(vec3 p){
    uvec3 n = floatBitsToUint(p);
    return float(h3(n).x) / float(MAX_UINT);
}

void mainImage(out vec4 f, in vec2 c){
    vec2 p1 = c/iResolution.xy;
    vec2 p2 = p1 - iTime * 1.0; 
    float col = 2.0 * h31(vec3(p2.y));
    f = vec4(col * 0.5, col * 0.5, col * 0.5, 0.2) * texture(iChannel0, p1);
}
