void burning_ship(in vec2 uv, out vec3 color)
{
    vec2 iter = vec2(0.0, 0.0);
    float abs_iter = 0.0;
    int i = 0;

    for(i = 0; i < 100; i++)
    {

        vec2 tmp = vec2(abs(iter.x), abs(iter.y));
        float real = (tmp.x * tmp.x) - (tmp.y * tmp.y);
        float imag = 2.0*tmp.x*tmp.y;

        // Uncomment for Mandelbrot
        /* float real = (iter.x * iter.x) - (iter.y * iter.y);
        float imag = 2.0*iter.x*iter.y; */

        iter = vec2(real, imag) + uv;

        if(length(iter) > 100.0)
        {
            abs_iter = 200.0;
            break;
        }
    }

    if(abs_iter == 0.0)
    {
        abs_iter = length(iter);  
    }


    if(abs_iter > 100.0)
    {
        color = vec3(float(i)/10.0, float(i)/50.0, 0.0);
    } else {
        color = vec3(0.25, 0.0, 0.0);
    }
}

const float PI = 3.1415;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 xy_norm = fragCoord/iResolution.xy;

    float x_scale = 3.0;
    float y_scale = -4.0 * float(iResolution.y) / float(iResolution.x);

    float shift_speed = 0.30*2.0*PI*iTime;
    float shift_freq = 1.6*2.0*PI*xy_norm.y;
    float shift_mag = 0.005;
    float x_shift = 0.6 + shift_mag*cos(shift_freq + shift_speed);
    float y_shift = 0.22;

    vec2 uv = vec2(x_scale, y_scale) * (xy_norm - vec2(x_shift, y_shift));

    vec3 color;
    burning_ship(uv, color);

    fragColor = vec4(color,1.0);
}
