float i(float a, float b, float t) { return (1.0-t)*a+t*b; }
float rand(vec2 co) { return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453); }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = floor(uv*10.0);
    float val0 = rand(pos + vec2(0,0));
    float val1 = rand(pos + vec2(1,0));
    float val2 = rand(pos + vec2(1,1));
    float val3 = rand(pos + vec2(0,1));

    vec2 pix = uv*10.0;
    float i0 = i(val0, val1, (fract(pix)*fract(pix)*(3.0-2.0*fract(pix))).x);
    float i1 = i(val3, val2, (fract(pix)*fract(pix)*(3.0-2.0*fract(pix))).x);
    float i3 = i(i0, i1, (fract(pix)*fract(pix)*(3.0-2.0*fract(pix))).y);
    fragColor = vec4(i3);
}
