void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //literally just some random math based on a formula
    //I found on wikipedia
    //https://en.wikipedia.org/wiki/Homogeneous_polynomial
    float term1 = pow(uv.x, 5.0);
    float term2 = 1.0 * pow(uv.x, 3.0) * pow(uv.y, 2.0);
    float term3 = 19.0 * uv.x * pow(uv.y, 4.0);

    float timeDelta = iTime * 0.3;

    vec3 col = vec3(
        fract(term1 + term2 + term3 + timeDelta + uv.y),
        fract(term1 + term3 - timeDelta + uv.x),
        timeDelta * 1.2
    );

    fragColor = vec4(col,1.0);
}
