const int AMOUNT = 2;

float rand (vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

vec2 rotate (vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 coord = 10.0 * (iResolution.xy / 2.0 - fragCoord.xy) / min(iResolution.x, iResolution.y);

    float len;

    for(int i = 0; i < AMOUNT; i++){
        len = length(vec2(coord.x, coord.y));

        coord.x = coord.x * 1.2 - cos(coord.y + sin(len)) + iTime / 1.0;
        coord.y = coord.y + 1.8 - sin(coord.x + cos(len)) + iTime / 1.5;
    }

    fragColor = vec4(cos(len * 2.0), cos(len - 1.0), cos(len / 2.0) , 1.0);
}
