const float W = 4.0;

float SdfCircle(vec2 p) {
    return length(p - vec2(W/2.0)) / (W/2.0) - 1.0;
}

float SampleSdf(vec2 p) {
    p *= W;
    vec2 b = floor(p);
    vec2 a = fract(p);
    return mix(
        mix(SdfCircle(b + vec2(0,0)), SdfCircle(b + vec2(0,1)), a.y),
        mix(SdfCircle(b + vec2(1,0)), SdfCircle(b + vec2(1,1)), a.y),
        a.x
    );
}

vec3 DistToColor(float dist) {
    return clamp(vec3(dist * 1e2), 0.0, 1.0);
}

vec3 SampleColorTex(vec2 p) {
    p *= W;
    vec2 b = floor(p);
    vec2 a = fract(p);
    return mix(
        mix(DistToColor(SdfCircle(b + vec2(0,0))), DistToColor(SdfCircle(b + vec2(0,1))), a.y),
        mix(DistToColor(SdfCircle(b + vec2(1,0))), DistToColor(SdfCircle(b + vec2(1,1))), a.y),
        a.x
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = fragCoord.xy / vec2(iResolution.xy);
    p.x *= 2.0;
    if (p.x < 1.0)
        fragColor = vec4(DistToColor(SampleSdf(p)), 1);
    else
        fragColor = vec4(SampleColorTex(p - vec2(1.0, 0.0)), 1);
}
