float t;
float pi = 3.14159265;
float r;
float g;
float b;
float s;
float d;
float i =0.;
vec2 f = vec2(0.,0.);
vec2 nf = vec2(0.,0.);
float zoom;
vec2 focal_point = vec2(-0.72096 ,0.238678);


void mainImage(out vec4 fragColor, in vec2 fragCoord){
    focal_point.x -= 0.0000001*log(sqrt(iTime));
   zoom =0.5 + 0.2*pow(1.5,iTime);
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv = uv * 4./zoom;
    uv = uv + focal_point;

    f = vec2(sqrt(abs(sin(0.5*log(sqrt(iTime))/pow(zoom,0.5))*0.5)),0.1);
    for(i = 0. ; i<(100.*sqrt(zoom)) ; i++){

        nf.x = f.x*f.x - f.y*f.y + uv.x;
        nf.y = (2.+0.01*sin(log(sqrt(iTime))/sqrt(zoom)))*f.x*f.y + uv.y;

        f=nf;

        if(f.x*f.x + f.y*f.y >40.){
            break;
        }

    }
    if(f.x*f.x + f.y*f.y >40.){
        r = 0.5+0.5*sin(pi*sqrt(i)/(10.)  ) + 0.2*sin(pi*i/(100.) + pi);
        g = 0.5+0.5*sin(pi*log(i)/(10.)  );
        b = 0.5+0.5*sin(pi*pow(i,0.8)/(10.) + pi);
    }

    r *= 0.8 + 0.2*sin(iTime / 2.);
    g *= 0.8 + 0.2*sin(iTime / 2.);
    g *=0.6;
    b *= 0.8;



    vec3 col = vec3( r , g , b );

    fragColor = vec4(col, 1.);

    zoom += 0.1;
}
