precision highp float;

#define FPS 30.0
#define DURATION 10.0

// Change this to true to also scale the rendered font.
#define ENABLE_SCALE false

// Author: Lohann Paterno Coutinho Ferreira
// This is a demonstrantion of the antialising method used in the GPU centered font rendering,
// It simulates a change in the pixel density, which is equivalent to downscale the font and zoom it.
// Original demo: https://www.shadertoy.com/view/msSyzV
//
// Article: GPU-Centered Font Rendering Directly from Glyph Outlines
// Link: https://jcgt.org/published/0006/02/02/

// All quadratic bezier points which compose the letter "a"
ivec2 beziers[62] = ivec2[](
    // Inner a
    ivec2(46127, 22077), // 0
    ivec2(46127, 33000),
    ivec2(46127, 33000), // 2
    ivec2(43680, 31929),
    ivec2(40261, 31271), // 4
    ivec2(36843, 30614),
    ivec2(28314, 29630), // 6
    ivec2(19416, 28541),
    ivec2(15453, 25883), // 8
    ivec2(11490, 23225),
    ivec2(11565, 18475), // 10
    ivec2(11931, 8976),
    ivec2(25257, 8715),  // 12
    ivec2(34994, 8744),
    ivec2(41740, 14176), // 14
    ivec2(46426, 17691),
    ivec2(46127, 22077),

    // Outer a
    ivec2(65535, 8599),  // 17
    ivec2(65535, 1278),
    ivec2(65535, 1278),  // 19
    ivec2(62843, 679),
    ivec2(61248, 450),   // 21
    ivec2(59653, 222),
    ivec2(57958, 232),   // 23
    ivec2(47456, 0),
    ivec2(46526, 9180),  // 25
    ivec2(35393, 0),
    ivec2(22864, 232),   // 27
    ivec2(12188, 323),
    ivec2(6148, 5098),   // 29
    ivec2(108, 9873),
    ivec2(0, 18243),     // 31
    ivec2(33, 30037),
    ivec2(12230, 34510), // 33
    ivec2(14946, 35571),
    ivec2(19109, 36282), // 35
    ivec2(23271, 36994),
    ivec2(34562, 38229), // 37
    ivec2(41204, 38944),
    ivec2(43684, 40422), // 39
    ivec2(46164, 41900),
    ivec2(46127, 44968), // 41
    ivec2(46127, 47524),
    ivec2(46127, 47524), // 43
    ivec2(45928, 56414),
    ivec2(30574, 56588), // 45
    ivec2(15320, 56762),
    ivec2(14224, 45782), // 47
    ivec2(3057, 45782),
    ivec2(3057, 45782),  // 49
    ivec2(3257, 54293),
    ivec2(7843, 58447),  // 51
    ivec2(15320, 65390),
    ivec2(30973, 65535), // 53
    ivec2(56928, 65332),
    ivec2(57160, 48919), // 55
    ivec2(57160, 13130),
    ivec2(57160, 13130), // 57
    ivec2(57260, 8482),
    ivec2(63142, 8366),  // 59
    ivec2(63940, 8308),
    ivec2(65535, 8599)
);

vec2 fetchBezier(int i)
{
    return vec2(beziers[i]) / 65535.0;
}

vec2 solvePoly(vec2 p1, vec2 p2, vec2 p3) {
    vec2 a = p1 - (p2 * 2.0) + p3;
    vec2 b = p1 - p2;
    float ra = 1.0 / a.y;
    float rb = 0.5 / b.y;

    float d = sqrt(max(b.y * b.y - a.y * p1.y, 0.0));
    float t1 = (b.y - d) * ra;
    float t2 = (b.y + d) * ra;

    // Verify if a.y is near zero
    if (abs(a.y) < 0.0001) {
        t1 = t2 = p1.y * rb;
    }

    return vec2(
        (a.x * t1 - b.x * 2.0) * t1 + p1.x,
        (a.x * t2 - b.x * 2.0) * t2 + p1.x
    );
}

uint calcRootCode(float y1, float y2, float y3) {
    uint i1 = floatBitsToUint(y1) >> 31;
    uint i2 = floatBitsToUint(y2) >> 30;
    uint i3 = floatBitsToUint(y3) >> 29;

    uint shift = (i2 & 2u) | (i1 & ~2u);
    shift = (i3 & 4u) | (shift & ~4u);

    return ((0x2E74u >> shift) & 0x0101u);
}

bool testCurve(uint code) {
    return (code != 0u);
}

bool testRoot1(uint code) {
    return ((code & 1u) != 0u);
}

bool testRoot2(uint code) {
    return code > 1u;
}

void updateCoverage(in vec2 pixelsPerEm, in vec2 p1, in vec2 p2, in vec2 p3, inout float coverage)
{
    uint code = calcRootCode(p1.y, p2.y, p3.y);
    if (testCurve(code)) {
        vec2 r = solvePoly(p1, p2, p3);
        r.x = clamp(r.x * pixelsPerEm.x + 0.5, 0.0, 1.0);
        r.y = clamp(r.y * pixelsPerEm.x + 0.5, 0.0, 1.0);
        if (testRoot1(code)) coverage += r.x;
        if (testRoot2(code)) coverage -= r.y;
    }

    p1 = vec2(p1.y, -p1.x);
    p2 = vec2(p2.y, -p2.x);
    p3 = vec2(p3.y, -p3.x);

    code = calcRootCode(p1.y, p2.y, p3.y);
    if (testCurve(code)) {
        vec2 r = solvePoly(p1, p2, p3);
        r.x = clamp(r.x * pixelsPerEm.y + 0.5, 0.0, 1.0);
        r.y = clamp(r.y * pixelsPerEm.y + 0.5, 0.0, 1.0);
        if (testRoot1(code)) coverage += r.x;
        if (testRoot2(code)) coverage -= r.y;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize fragment position between 0 and 1
	vec2 position = fragCoord.xy / iResolution.y;
    position.x -= ((iResolution.x - iResolution.y) / iResolution.y) / 2.0;
    position *= 2.0;
    position -= 0.5;

    // Animation Timeline, outputs a values between 0 and 1
    float timeline = abs(mod(floor(iTime * FPS), (FPS * DURATION + 1.0)) - (FPS * DURATION / 2.0)) / (FPS * DURATION / 2.0);
    timeline = sqrt(timeline);
    timeline = sqrt(timeline);
    timeline = timeline * 1.155 - 0.15;
    timeline = clamp(timeline, 0.0, 1.0);

    // Simulate scaling the font size
    float bbox_size = floor(iResolution.y / 2.0); // Size of the font bounding box in pixels
    float font_size = floor(mix(bbox_size, 10.0, timeline)); // font-size in pixels
    if (ENABLE_SCALE) {
        float scale = bbox_size / font_size;
        position *= scale;
        position -= (scale-1.0) / 2.0;
    }
    position = floor(position * font_size) + 0.5;
    position /= font_size;

    // Antialiasing
    vec2 pixelsPerEm = vec2(font_size);

    // Calculate pixel coverage
    float coverage = 0.0;
    for (int i=0;i<=14;i+=2) {
        vec2 p0 = fetchBezier(i+0) - position;
        vec2 p1 = fetchBezier(i+1) - position;
        vec2 p2 = fetchBezier(i+2) - position;
        updateCoverage(pixelsPerEm, p0, p1, p2, coverage);
    }
    for (int i=17;i<=59;i+=2) {
        vec2 p0 = fetchBezier(i+0) - position;
        vec2 p1 = fetchBezier(i+1) - position;
        vec2 p2 = fetchBezier(i+2) - position;
        updateCoverage(pixelsPerEm, p0, p1, p2, coverage);
    }

    // Normalize coverage
    coverage = sqrt(clamp(abs(coverage) * 0.5, 0.0, 1.0));

    // Final color
    fragColor = vec4(coverage, coverage, coverage, 1.0);
}
