vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.8816,0.557);

    return a + b*cos( 14.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 0.1 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {
        float angle = -iTime * 0.04; // Inverse rotation
        mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
        uv = fract(rot * uv * 9.17) - 0.01;

        float d = length(uv) * exp(-length(uv0));

        // Convert uv to polar coordinates
        float r = length(uv);
        float theta = atan(uv.y, uv.x);

        // Apply Fibonacci spiral to the color
        vec3 col = palette(length(uv0) + i*.4 + iTime*.4 + 0.2*sqrt(theta));

        d = sin(d*128. + iTime)/8.;
        d = abs(d);

        d = pow(0.01 / d, 1.8);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
