void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;

    // Time varying pixel color
    vec3 col = 0.75 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col/=(fragCoord.x*fragCoord.x,fragCoord.y*fragCoord.y,cos(sqrt(uv.x*uv.y)));
    // Output to screen  
    vec3 balls = texture(iChannel0, uv).rgb;
    fragColor = vec4(sqrt((sqrt(balls.x*balls.z)*log(col))),1.0);
}

