#define PI 3.141592

mat2 rotate2d(in float a){
    return mat2(cos(a), -sin(a),
            sin(a), cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 4.0;
    uv -= 2.0;
    uv.x *= iResolution.x/iResolution.y;

    //uv = rotate2d(iTime/16.0) * uv;

    //Build the striped circle
    float cfreq = 1.9*atan(abs(uv.y), abs(uv.x));


    //Kynd - www.flickr.com/photos/kynd/9546075099/
    float osc = 1.5 * pow(cos(iTime/ 2.0), 2.0);

    //- length(vec2(6.0*abs(cos(iTime/8.0))
    float displacement = length(vec2(osc*mod(iTime/4.0, 6.0)));

    float ds = mod(length(uv) - displacement, cfreq);
    float e = smoothstep(cfreq, 0.3-0.02, ds);  //anti alias outer ring

    float r = 0.8;
    ds = smoothstep(r, r+0.02, ds) * e;

    //
    float angle = atan(uv.y, uv.x);

    //palette
    //ref: https://iquilezles.org/articles/palettes/
    vec3 a = vec3(0.8, 0.5, 0.5);
    vec3 b = vec3(0.3, 0.8, 0.3);
    vec3 c = vec3(0.2, 0.2, 0.8);
    vec3 d = vec3(1.0, 0.8, 0.5);

    vec3 palette = a + b*cos(iTime*c + d);

    //Finalising
    vec3 col = vec3(ds + 0.04*abs(angle)) * palette;

    // Output to screen
    fragColor = vec4(col,1.0);
}
