float f_n_random(vec2 o){
    float n = fract(o.x*0.369+o.y*0.369)*0.369;
    n = fract(o.x*n*936.+o.y*n*9369.+o.x*n+o.y*33399.);
    n = fract(pow(n, 39.369));
    return n;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pix_coo_nor = (fragCoord.xy - iResolution.xy*0.5) /iResolution.y;
    vec2 o_mou_coo_nor = (iMouse.xy - iResolution.xy*0.5) /iResolution.y;
    fragColor = vec4(f_n_random(o_pix_coo_nor+iTime*0.000001));
}
