void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // center of the screen
    vec2 origin = iResolution.xy / 2.0f;

    // speed
    float update = iTime * 10.0;

    origin.x = iResolution.x*0.5+0.5*cos(iTime*fragCoord.x);
    origin.y = iResolution.y*0.5+0.5*sin(iTime*fragCoord.y);

    float angle = (uv.x - (0.5+0.5*cos(iTime))) * (uv.y - (0.5+0.5*sin(iTime)));
    float f = sqrt(0.5+0.5*+sin(angle*distance(fragCoord.xy, origin) + update ));

    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 black = vec4(0.0, 0.0, 0.0, 1.0);

    fragColor = mix(white, black, round(f));
}
