vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 add(vec2 a, vec2 b)
{
    return vec2(a.x+b.x, a.y+b.y);
}

vec2 mul(vec2 a, vec2 b)
{
    return vec2(a.x*b.x-a.y*b.y, a.x*b.y + a.y*b.x);
}

vec2 func(vec2 z, vec2 c)
{
    return add(mul(z,z),c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float zoom = 2./pow(1.6,0.);
    uv *= zoom; 
    vec2 focus = vec2(0.548, 0.50008);
    focus = vec2(0.,0.);
    uv -= focus;


    float angle = iTime * 0.2;
    vec2 c = vec2(sin(angle), cos(angle))*0.75;
    vec2 z = uv;

    float its = 0.;

    for(float i = 0.; i < 1000.; i++)
    {
       its++;
       z = func(z, c);

       if (length(z)>4.)
           break;
    }

    vec2 x = vec2(1,0);
    float a = acos(dot(z,x) / (length(x)*length(z)));
    if(z.y<0.)
        a = 3.141 + acos(-dot(z,x) / (length(x)*length(z)));
    float b = length(z);
    float s = pow(1.05, -b);

    a = pow(its, 0.4);
    vec3 col = hsv2rgb(vec3(a/(2.*3.141),1.,1.));
    if(b<1.)
        col = vec3(0.);
    fragColor = vec4(col,1.0);
}
