void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize the coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Get the audio level
    float audioLevel = texture(iChannel0, vec2(0.0)).r;

    // Create a pulsating effect based on audio level
    float pulsate = smoothstep(0.2, 0.0, sin(iTime) + audioLevel);

    // Create a horror effect by distorting the UV coordinates
    float distortion = sin(uv.y * iResolution.y * 0.01 + iTime) * pulsate;
    uv.x += distortion * 0.02;

    // Create a color gradient
    vec3 color = vec3(0.0);
    color.r = uv.x;
    color.g = uv.y;
    color.b = 0.5;

    // Apply the color and pulsating effect
    fragColor = vec4(color * pulsate, 1.0);
}
