precision highp float;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 r = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);

  vec3 color1 = vec3(0, 38.0/255.0, 84.0/255.0); // Blue
  vec3 color2 = vec3(1.0, 1.0, 1.0); // White
  vec3 color3 = vec3(237.0/255.0, 41.0/255.0, 57.0/255.0); // Red

  vec3 pixel;

  if( r.x < 1.0/3.0) {
    pixel = color1;
  } else if( r.x < 2.0/3.0 ) {
    pixel = color2;
  } else {
    pixel = color3;
  }

  fragColor = vec4(pixel, 1.0);
}
