/*
Direct Light Integration


Controls
    WASD to move
        Space to move faster
    Mouse to look around
    Uncomment "//#define PathTracing" to path trace instead
*/


//#define PathTracing


vec3 Integrate(vec3 P, vec3 N, int TriangleIndex) {
    //Integral
    float NEAR = 0.00001;
    vec3 Integral = vec3(0.);
    TriangleI VTris[(NTris-1)*2]; //Clipped geometry
    Triangle3 OTris[(NTris-1)*3]; //Negative and positive emissive triangles
    //Clip all geometry
    int NVTris = 0; int NOTris;
    for (int i=0; i<NTris; i++) {
        //For all triangles
        if (i==TriangleIndex) continue;
        Triangle TriS = Triangles[i];
        vec3 P0 = TriS.p0;
        vec3 P1 = TriS.p1;
        vec3 P2 = TriS.p2;
        vec3 DotV = vec3(dot(N,TriS.p0-P),dot(N,TriS.p1-P),dot(N,TriS.p2-P));
        bvec3 DotVb = bvec3(DotV.x>NEAR,DotV.y>NEAR,DotV.z>NEAR);
        float NFront = dot(vec3(1.),vec3(DotVb));
        if (NFront<0.5) {
            //Triangle is behind P
            continue;
        } else if (NFront<1.5) {
            //1 point in front of P
            if (!DotVb.x) {
                if (DotVb.y) {
                    P1 = TriS.p0;
                    P0 = TriS.p1;
                } else {
                    P2 = TriS.p0;
                    P0 = TriS.p2;
                }
            }
            vec3 IP0 = P0+(P1-P0)*(-dot(P0-P,N)/dot(P1-P0,N));
            vec3 IP1 = P0+(P2-P0)*(-dot(P0-P,N)/dot(P2-P0,N));
            VTris[NVTris] = TriangleI(P0,IP0,IP1,i);
            NVTris += 1;
        } else if (NFront<2.5) {
            //2 points in front of P
            if (DotVb.x) {
                if (!DotVb.y) {
                    P1 = TriS.p2;
                    P2 = TriS.p1;
                }
            } else {
                P0 = TriS.p2;
                P1 = TriS.p1;
                P2 = TriS.p0;
            }
            vec3 IP0 = P0+(P2-P0)*(-dot(P0-P,N)/dot(P2-P0,N));
            vec3 IP1 = P1+(P2-P1)*(-dot(P1-P,N)/dot(P2-P1,N));
            VTris[NVTris] = TriangleI(P0,IP0,P1,i);
            VTris[NVTris+1] = TriangleI(P1,IP0,IP1,i);
            NVTris += 2;
        } else {
            //Triangle is completely in front of P
            VTris[NVTris] = TriangleI(TriS.p0,TriS.p1,TriS.p2,i);
            NVTris += 1;
        }
    }
    //Integrate
    for (int SubjectIndex=0; SubjectIndex<NVTris; SubjectIndex++) {
        //For all visible triangles
        TriangleI CTri = VTris[SubjectIndex];
        if (CTri.Index<6 || CTri.Index==TriangleIndex) continue;
        vec3 Emissive = ((CTri.Index==7)?E7:E6);
        Integral += Emissive*abs(SolidAngle(P,N,Triangle(CTri.p0,CTri.p1,CTri.p2,vec3(0.))));
        //Attributes
        vec3 CNor = normalize(cross(CTri.p1.xyz-CTri.p0.xyz,CTri.p2.xyz-CTri.p0.xyz));
        CNor *= sign(dot(P-CTri.p0,CNor));
        float CTanLen = length(CTri.p1-CTri.p0);
        vec3 CTan = (CTri.p1-CTri.p0)/CTanLen;
        vec3 CBit = normalize(cross(CTan,CNor));
        //Occluder
        int tmpNOTris = 1;
        int NOTris = 1;
        OTris[0] = Triangle3(CTri.p0,CTri.p1,CTri.p2,1.);
        for (int OIndex=0; OIndex<NVTris; OIndex++) {
            //For all visible triangles
            TriangleI OTri = VTris[OIndex];
            if (OTri.Index==CTri.Index) continue;
            tmpNOTris = NOTris;
            for (int oti=0; oti<tmpNOTris; oti++) {
                //For every occluder triangle
                Triangle3 OT = OTris[oti];
                //Projection
                float ssds,ssdC; vec3 VDot,TNormS,pp0,pp1,pp2,IFDir,ssns,ssnC; vec3 TPOut[6],TPIn[6];   
                //Triangle intersection: https://en.wikipedia.org/wiki/Sutherland%E2%80%93Hodgman_algorithm
                int NPoints = 3; int OutPoints = 3; pp0 = OTri.p0; pp1 = OTri.p1; pp2 = OTri.p2;
                vec3 LTan,tmpp0,cpp,cppPrev,cppIntersect;
                TPOut = vec3[6](OT.p0,OT.p1,OT.p2,vec3(0.),vec3(0.),vec3(0.));
                for (int SH=0; SH<3; SH++) { //For every clip-line in the clipping triangle
                    LTan = normalize(cross(pp0-P,pp0-pp1));
                    LTan = LTan*sign(dot(LTan,pp2-pp0));
                    TPIn = TPOut;
                    NPoints = OutPoints;
                    OutPoints = 0;
                    cppPrev = TPIn[NPoints-1];
                    for (int ci=0; ci<NPoints; ci++) { //For every point in the subject polygon
                        cpp = TPIn[ci];
                        cppIntersect = cpp-(cpp-cppPrev)*dot(cpp-pp0,LTan)/dot(cpp-cppPrev,LTan);
                        if (dot(cpp-pp0,LTan)>=0.) {
                            if (dot(cppPrev-pp0,LTan)<0.) {
                                //Enough precision
                                TPOut[OutPoints] = cppIntersect;
                                OutPoints += 1;
                            }
                            TPOut[OutPoints] = cpp;
                            OutPoints += 1;
                        } else if (dot(cppPrev-pp0,LTan)>=0.) {
                            TPOut[OutPoints] = cppIntersect;
                            OutPoints += 1;
                        }
                        cppPrev = cpp;
                    }
                    tmpp0 = pp0;
                    pp0 = pp1;
                    pp1 = pp2;
                    pp2 = tmpp0;
                }
                //Add occluded geometry as signed emissive triangles
                if (OutPoints>=3) {
                    //In front test
                    IFDir = normalize((TPOut[0]+TPOut[1]+TPOut[2])/3.-P);
                    bool BTCT = TriangleA(P,IFDir,CTri.p0,CTri.p1,CTri.p2,ssdC,ssnC);
                    bool BTst = TriangleA(P,IFDir,OTri.p0,OTri.p1,OTri.p2,ssds,ssns);
                    if (ssdC>=ssds) {
                        //A clipped polygon -> 1 or more triangles
                        pp0 = TPOut[0];
                        pp1 = TPOut[1];
                        for (int it=2; it<OutPoints; it++) {
                            Triangle3 tmpWT = Triangle3(pp0,pp1,TPOut[it],-OT.Sign);
                            Integral -= Emissive*abs(SolidAngle(P,N,Triangle(tmpWT.p0,tmpWT.p1,tmpWT.p2,vec3(0.))))*OT.Sign;
                            OTris[NOTris] = tmpWT;
                            NOTris += 1;
                            pp1 = TPOut[it];
                        }
                    }
                }
            }
        }
    }
    //Return
    return max(vec3(0.),Integral)/(2.*PI);
}

vec3 acesFilm(vec3 x) {
    //Aces film curve
    return clamp((x*(2.51*x+0.03))/(x*(2.43*x+0.59)+0.14),0.,1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    //Epic animated triangles
    float tAngle = iTime*0.7;
    int TriIndex = 2;
    for (float fti=0.; fti<2.5; fti++) {
        tAngle += 2.0943951;
        vec3 RotVec = vec3(sin(tAngle),cos(tAngle),0.);
        vec3 RotTan = vec3(-RotVec.y*0.,RotVec.x*0.+1.,0.)*0.79;
        vec3 RotPos = vec3(0.,1.6,0.)+RotVec*0.8;
        Triangles[TriIndex] = Triangle(RotPos+RotTan,RotPos-RotTan,RotPos+vec3(0.4,0.,-1.5),vec3(0.99));
        TriIndex += 1;
    }
    float Sine = sin(iTime*1.5);
    float Scale = (pow(abs(Sine),0.25)*sign(Sine)*0.5+0.5);
    vec3 RotVec = vec3(-0.6,0.,1.)*(0.8999+Scale*0.9);
    vec3 RotPos = vec3(-1.,0.,-1.8);
    Triangles[5] = Triangle(RotPos+RotVec,RotPos-RotVec,RotPos+RotVec*0.5+vec3(0.2,1.5+Scale*1.5,0.),vec3(0.99));
    //Pos
    vec3 Color = vec3(0.);
    vec3 SunDir = texture(iChannel0,vec2(5.5,0.5)*IRES).xyz;
    vec3 Pos = texture(iChannel0,vec2(3.5,0.5)*IRES).xyz;
    vec3 Eye = texture(iChannel0,vec2(2.5,0.5)*IRES).xyz;
    vec3 Dir = normalize(vec3((fragCoord*IRES*2.-1.)*(ASPECT*CFOV),1.)*TBN(Eye));
    //Render scene
    HIT Pixel = TraceRay(Pos,Dir,-1);
    if (Pixel.D<9990.) {
        if (Pixel.Index==7) {
            Color = E7;
        } else if (Pixel.Index==6) {
            Color = E6;
        } else {
            vec3 Integral = vec3(0.);
            #ifdef PathTracing
                //Path tracing
                vec3 Diffuse = vec3(0.);
                float NSamples = 64.;
                for (float i=0.; i<NSamples-0.5; i++) {
                    vec3 rdir = RandSample(texture(iChannel3,(fragCoord+i*(vec2(314.734,517.394)))/1024.).zw)*TBN(Pixel.N);
                    HIT RefHit = TraceRay(Pixel.P+Pixel.N*0.001,rdir,Pixel.Index);
                    if (RefHit.D<99990.) {
                        if (RefHit.Index==6) Diffuse += E6;
                        if (RefHit.Index==7) Diffuse += E7;
                    }
                }
                Integral = Diffuse/NSamples;
            #else
                //Integral
                Integral += Integrate(Pixel.P,Pixel.N,Pixel.Index);
            #endif
            //Color
            Color = Pixel.C*Integral;
        }
    } else {
        //Sky
        Color = vec3(0.);
    }
    //Output
    fragColor = vec4(pow(acesFilm(Color),vec3(0.45)),1.);
}

//Settings
const float FOV = 0.7; //In radians

//Other vars
const int NTris = 8;
const vec3 E7 = vec3(0.3,0.9,1.)*3.;
const vec3 E6 = vec3(1.,0.35,0.05)*3.;
const float CFOV = tan(FOV);
const float PI = 3.141592653;
#define RES iResolution.xy
#define IRES (1./iResolution.xy)
#define ASPECT vec2(iResolution.x/iResolution.y,1.)

struct HIT { float D; vec3 P; vec3 N; vec3 C; int Index; };
struct Triangle { vec3 p0; vec3 p1; vec3 p2; vec3 C; };
//struct Triangle2 { vec2 p0; vec2 p1; vec2 p2; float Sign; };
struct Triangle3 { vec3 p0; vec3 p1; vec3 p2; float Sign; };
struct TriangleI { vec3 p0; vec3 p1; vec3 p2; int Index; };

Triangle Triangles[NTris]=Triangle[NTris](
        Triangle(vec3(-64.,0.,3.),vec3(0.,0.,-64.),vec3(64.,0.,3.),vec3(1.)), //Floor
        Triangle(vec3(-4.,0.,0.),vec3(4.,0.,0.),vec3(0.,1024.,0.),vec3(1.)), //Wall
        //Occluders
        Triangle(vec3(0.,0.6,0.5),vec3(-0.3,0.000,0.5),vec3(0.3,0.,0.5),vec3(0.4,0.4,1.)),
        Triangle(vec3(0.,0.6,0.5),vec3(-0.3,0.000,0.5),vec3(0.3,0.,0.5),vec3(0.4,0.4,1.)),
            Triangle(vec3(-0.3,0.,0.6),vec3(-0.1,0.,1.),vec3(-0.2,0.9,0.75),vec3(0.4,0.4,1.)),
            Triangle(vec3(0.3,0.,-1.4),vec3(0.9,0.,-2.9),vec3(0.6,1.7,-2.15),vec3(0.4,0.4,1.)),
        //Emissive
            Triangle(vec3(-2.5,0.0001,-0.0001),vec3(-2.5,0.0001,-2.5),vec3(-2.5,2.5,-2.5),vec3(1.)), //Quad
            Triangle(vec3(-2.5,0.0001,-0.0001),vec3(-2.5,2.5,-0.0001),vec3(-2.5,2.5,-2.5),vec3(1.))
        );

mat3 TBN(vec3 N) {
    vec3 Nb,Nt;
    if (abs(N.y)>0.999) {
        Nb = vec3(1.,0.,0.);
        Nt = vec3(0.,0.,1.);
    } else {
    	Nb = normalize(cross(N,vec3(0.,1.,0.)));
    	Nt = normalize(cross(Nb,N));
    }
    return mat3(Nb.x,Nt.x,N.x,Nb.y,Nt.y,N.y,Nb.z,Nt.z,N.z);
}

vec3 TBN(vec3 N, out vec3 O) {
    O = normalize(cross(N,vec3(0.001,1.,0.)));
    return normalize(cross(O,N));
}

vec3 RandSample(vec2 v) {
    float theta = sqrt(v.x);
    float phi = 2.*3.14159*v.y;
    float x = theta*cos(phi);
    float z = theta*sin(phi);
    return vec3(x,z,sqrt(max(0.,1.-v.x)));
}

bool TriangleA(vec3 p, vec3 d, vec3 p0, vec3 p1, vec3 p2, out float hit, out vec3 triN) {
    //From https://github.com/Jojendersie/gpugi/blob/5d18526c864bbf09baca02bfab6bcec97b7e1210/gpugi/shader/intersectiontests.glsl#L63
    vec3 e0 = p1-p0;
    vec3 e1 = p0-p2;
    triN = cross(e1,e0);
    vec3 e2 = (1./dot(triN,d))*(p0-p);
    vec3 i  = cross(d,e2);
    vec3 barycentricCoord = vec3(0.);
    barycentricCoord.y = dot(i,e1);
    barycentricCoord.z = dot(i,e0);
    barycentricCoord.x = 1.-(barycentricCoord.z+barycentricCoord.y);
    hit = dot(triN,e2);
    return  (hit>=0.) && all(greaterThanEqual(barycentricCoord,vec3(0.)));
    //return (hit>NEAR) && all(greaterThanEqual(barycentricCoord,vec3(0.)));
}

HIT TraceRay(vec3 Pos, vec3 Dir, int IgnoreIndex) {
    //Ray tracing against geometry
    float sd; float D = 10000.; Triangle Sample; vec3 sn;
    HIT OUT = HIT(D,vec3(0.),vec3(0.),vec3(0.),-1);
    for (int i=0; i<NTris; i++) {
        if (i==IgnoreIndex) continue;
        Sample = Triangles[i];
        if (TriangleA(Pos,Dir,Sample.p0,Sample.p1,Sample.p2,sd,sn)) {
            if (sd<D) {
                D = sd;
                OUT = HIT(D,Pos+Dir*D,sn,Sample.C,i);
            }
        }
    }
    OUT.N = normalize(OUT.N)*sign(dot(-Dir,OUT.N));
    return OUT;
}

float SolidAngle(vec3 P, vec3 N, Triangle Tri) {
    //Returns the solid angle for a visible triangle
    vec3 v0 = normalize(Tri.p0-P);
    vec3 v1 = normalize(Tri.p1-P);
    vec3 v2 = normalize(Tri.p2-P);
    float ret = abs(dot(N,normalize(cross(v0,v1)))*acos(dot(v0,v1))+
                dot(N,normalize(cross(v1,v2)))*acos(dot(v1,v2))+
                dot(N,normalize(cross(v2,v0)))*acos(dot(v2,v0)));
    return ((isnan(ret))?0.:ret);
}

//Storage

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 Color = texture(iChannel0,fragCoord.xy/iResolution.xy);
    if (iFrame==0) { //Initialization
        if (fragCoord.x<10. && fragCoord.y<1.) { //Store vars
            if (fragCoord.x<1.) Color = vec4(0.,0.,0.,0.); //Mouse
            else if (fragCoord.x<2.) Color = vec4(-0.5,-0.05,0.,0.); //Player Eye (Angles)
            else if (fragCoord.x<3.) Color = vec4(0.,0.,0.,1.); //Player Eye (Vector)
            else if (fragCoord.x<4.) Color = vec4(-0.3,3.,-4.,1.); //Player Pos
        }
    } else { //Update
		if (fragCoord.x<16. && fragCoord.y<1.) { //Update vars
            if (fragCoord.x<1.) { //Mouse
                if (iMouse.z>0.) { //BÃ¶rjat klicka
                    if (Color.w==0.) {
                    	Color.w = 1.;
                    	Color.xy = iMouse.zw;
                    }
                } else Color.w = 0.;
            } else if (fragCoord.x<2.) { //Player Eye (Angles)
                vec4 LMouse = texture(iChannel0,vec2(0.5,0.5)*IRES);
                if (LMouse.w==0.)  Color.zw = Color.xy;
                if (LMouse.w==1.) {
                	//Y led
                	Color.x = Color.z+(iMouse.y-LMouse.y)*0.01;
                	Color.x = clamp(Color.x,-2.8*0.5,2.8*0.5);
                	//X led
                	Color.y = Color.w-(iMouse.x-LMouse.x)*0.02;
               		Color.y = mod(Color.y,3.1415926*2.);
                }
            } else if (fragCoord.x<3.) { //Player Eye (Vector)
                vec3 Angles = texture(iChannel0,vec2(1.5,0.5)*IRES).xyz;
                Color.xyz = normalize(vec3(cos(Angles.x)*sin(Angles.y),
                  			   			sin(Angles.x),
                  			   			cos(Angles.x)*cos(Angles.y)));
            } else if (fragCoord.x<4.) { //Player Pos
                float Speed = iTimeDelta;
                if (texelFetch(iChannel1,ivec2(32,0),0).x>0.) Speed = 5.*iTimeDelta;
                vec3 Eye=texture(iChannel0,vec2(2.5,0.5)*IRES).xyz;
                if (texelFetch(iChannel1,ivec2(87,0),0).x>0.) Color.xyz += Eye*Speed; //W
                if (texelFetch(iChannel1,ivec2(83,0),0).x>0.) Color.xyz -= Eye*Speed; //S
                vec3 Tan = normalize(cross(vec3(Eye.x,0.,Eye.z),vec3(0.,1.,0.)));
                if (texelFetch(iChannel1,ivec2(65,0),0).x>0.) Color.xyz -= Tan*Speed; //A
                if (texelFetch(iChannel1,ivec2(68,0),0).x>0.) Color.xyz += Tan*Speed; //D
            }
        }
    }
    fragColor = Color;
}
