const float M_PI = 3.14159265358979323846264338327950288;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
// Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r = abs(.5 + .4 * sin(iTime) * sin(iTime + 4. * M_PI * uv.x) - uv.y);    
    r = smoothstep(0., .05, r);
    float g = abs(.5 + .4 * sin(iTime * .9) * sin(iTime * .9 + 4. * M_PI * uv.x) - uv.y);    
    g = smoothstep(0., .05, g);
    float b = abs(.5 + .4 * sin(iTime * 1.1) * sin(iTime * 1.1 + 4. * M_PI * uv.x) - uv.y); 
    b = smoothstep(0., .05, b);

    // Output to screen
    fragColor = vec4(r, g, b, 1.);
}
