void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float scale = 4.;

    vec2 uv = fragCoord/iResolution.y;
    uv *= scale;

    float mask = mod(uv.y, scale / 2.);
    mask /= scale / 2.;
    mask = mask < 0.5 ? 1. : 0.;

    uv.x += mask * 0.5;

    uv.x -= iTime * .5;

    vec2 localUV = fract(uv);

    //float value = step(0.5, (uv.y));

    /*
    uv = fract(uv);
