    uv *= 2.;
    */
    float aspect = iResolution.x / iResolution.y;
    //localUV.x /= aspect;
    //uv -= vec2(0.5 * aspect, 0.5);


    //float value = length(uv);
    //value = 1. - step(0.5, value);


    localUV -= 0.5;
    localUV *= 2.;

    float circles = length(localUV);
    circles = 1. - step(0.5, circles);

    vec3 color = mix(vec3(1, 0, 0), vec3(0.4, 0.7, 1), 1. - circles);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
