ivec2 c;
const float PI = 3.1415926;
uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
uvec3 u = uvec3(1, 2, 3);
const uint M = 0xffffffffu;

uvec2 h(uvec2 n) {
    n ^= (n.yx << u.xy);
    n ^= (n.yx >> u.xy);
    n *= k.xy;
    n ^= (n.yx << u.xy);
    return n * k.xy;
}

float s(vec2 p) {
    uvec2 n = floatBitsToUint(p);
    return float(h(n).x) / float(M);
}

float vn(vec2 p) {
    vec2 n = floor(p);
    float[4] v;
    for (int j = 0; j < 2; j++) {
        for (int i = 0; i < 2; i++) {
            v[i + 2 * j] = s(n + vec2(i, j));
        }
    }
    vec2 f = fract(p);
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    return mix(mix(v[0], v[1], f[0]), mix(v[2], v[3], f[0]), f[1]);
}

float gt(vec2 l, vec2 p) {
    uvec2 n = floatBitsToUint(l);
    uint i = h(n).x >> 29;
    float u = 0.92387953 * (i < 4u ? p.x : p.y);  
    float v = 0.38268343 * (i < 4u ? p.y : p.x);  
    return ((i & 1u) == 0u ? u : -u) + ((i & 2u) == 0u ? v : -v);
}

float pn(vec2 p) {
    vec2 n = floor(p);
    vec2 f = fract(p);
    float[4] v;
    for (int j = 0; j < 2; j++) {
        for (int i = 0; i < 2; i++) {
            v[i + 2 * j] = gt(n + vec2(i, j), f - vec2(i, j));
        }
    }
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    return iTime * mix(mix(v[0], v[1], f[0]), mix(v[2], v[3], f[0]), f[1]) + 0.5;
}

float fbm(vec2 p, float g) {
    float val = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for (int i = 0; i < 4; i++) {
        val += amp * (vn(freq * p) - 0.5);
        amp *= g;
        freq *= 2.01;
    }
    return 0.5 * val + 3.5;
}

float bs(vec2 p) {
    return mod(iTime, 20.0) < 10.0 ? fbm(p, 0.5) : pn(p);
}

float cv(float v) {
    float t = abs(mod(0.1 * iTime, 2.0) - 1.0);
    float n = floor(8.0 * t);
    return
        c == ivec2(0, 0) ? 0.1 * sin(4.0 * PI * v + iTime) + 0.9 :
        v;
}

void mainImage(out vec4 f, in vec2 c) {
    vec2 p = c.xy / iResolution.xy;
    vec3 col = vec3(cv(float(texture(iChannel0, p))));
    f = vec4(col, 1.0);
}
