//https://stackoverflow.com/questions/72479232/shader-fails-on-mobile
precision highp float; // Define float precision
precision highp int;   // Define int precision

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float n = 5.;

    float n_x = mod(uv.x * (iResolution.x/n), 1.0);
    if(n_x >= (1./n)*0.){
        fragColor = vec4(1.);
    }
    if(n_x >= (1./n)*1.){
        fragColor = vec4(0.);
    }
    if(n_x >= (1./n)*2.){
        fragColor = vec4(0.5);
    }
    if(n_x >= (1./n)*3.){
        fragColor = vec4(mod(iTime,1.));
    }
    if(n_x >= (1./n)*4.){
        fragColor = vec4(float(mod(iTime,2.) > 1.));
    }


    // Output to screen
}
