#define sS(a,v)   ( 2.* smoothstep( a, -a, (v)*R.y ) - 1. )                // signed smoothstep
#define S(k)    sS( 2., abs( l - k*L - .01*L* sin(2.*a*6.28) ) - .007*L )  // sinusoidal circle

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, U = ( u+u - R ) / R.y;        // normalized coordinates     
    float l = length(U),  a = 30.* atan(U.y,U.x)/ 6.283,    // polar coords
          z = floor(2.*log2(l)), L = exp2(z/2.);            // circles radii
    int(z)%2 < 1 ? a = -a : a;                              // alternate sin orientation

    O = vec4( .5 + .5* sS( 8./l, abs( fract(a)-.5 ) -.25 )  // rays
                     * S( 1.1 ) * S( 1.2 )                  // pairs of circles
            ) ;
}
