#define PI 3.14159


vec2 UV(vec2 coord)
{
    return (2. * coord - iResolution.xy) / min(iResolution.x , iResolution.y);
}

float onePixel(float w)
{
    float x = 5. / max(iResolution.x,iResolution.y) ;
    return x;
}

float Axis(vec2 uv , float w)
{
    if(abs(uv.x) < w || abs(uv.y) < w)
    {
        return 1.;
    }
    return 0.;
}


vec3 Grid(vec2 uv,float w)
{
    vec3 color = vec3(0.);
    if(uv.x < w || 1. -uv.x < w)
    {
        color = vec3(1.);
    }
    else if(uv.y < w || 1. -uv.y < w)
    {
        color = vec3(1.);
    }
    return color;
}

float SIN(vec2 uv)
{
    if(abs(uv.y - sin(3. * uv.x)) < 0.01)
    {
        return 1.;
    }
    return 0.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(1.);
    vec2 UV = UV(fragCoord);
    float num = 2.;
    float op = onePixel(num);
    vec2 cell = fract(UV * num);

    color = Grid(cell,op);
    color = mix(color , vec3(0.,0.5,0.) , Axis(UV,op));
    color = mix(color , vec3(1.0,0.5,0.) , SIN(UV * fract(iTime)));

    fragColor = vec4(color,1.);
}
