float f(in float x) {
    return sqrt(x - (x * x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 v = uv / 2.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime + sin(uv.xyx) + vec3(0,2,4));

    // Output to screen
    fragColor = vec4(f(distance(vec2(1,1) * fract(abs(sin(cos(iTime)))), uv)) * col * 2.0 + 0.5,1.0);
}
