// Glitch Shake Effect by Akascape

#define Mode 2 //Modes: 0,1,2
const float Frequency    = 5.;
const float ChromaOffset = .01;
const float Interval     = .5;
const float Power        = .05;

float hash11(float x) {
    return fract(120.*sin(360.*x));
}

float noise(float x) {
    float p = floor(x);
    float q = fract(x);
    return mix(hash11(p), hash11(p+1.), q); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.); 
    vec2 screen = uv;
    float var = Frequency * iTime;
    float random = step(Interval, noise(var));

    if (Mode==0) {
        screen.x += hash11(iTime+screen.x)*random*Power;
    }
    else if (Mode==1) {
        screen.x += hash11(iTime+screen.y)*random*Power;
    }
    else {
        screen.x += hash11(iTime+screen.x)*random*Power;
        screen.y += hash11(iTime+screen.y)*random*Power;
    }

    col = texture(iChannel0, screen).rgb;
    vec2 offset = vec2(noise(var+50.), noise(var-50.))*ChromaOffset*random;

    col.r = texture(iChannel0, screen+offset).r;
    col.g = texture(iChannel0, screen-offset).g;
    col.b = texture(iChannel0, screen-offset).b;

    fragColor = vec4(col,1.0);
}
