void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Define the center and radius of each buttock
    vec2 leftCenter = vec2(iResolution.x * 0.4, iResolution.y * 0.5);
    vec2 rightCenter = vec2(iResolution.x * 0.6, iResolution.y * 0.5);
    float radius = min(iResolution.y, iResolution.x) * 0.25;

    // Calculate the distance from the fragment coordinate to the buttocks centers
    float leftDistance = length(fragCoord - leftCenter);
    float rightDistance = length(fragCoord - rightCenter);

    // Define the color of the buttocks
    vec3 color = vec3(0.88, 0.56, 0.36);

    // Set the color within each buttocks region, based on distance from center
    if (leftDistance < radius || rightDistance < radius)
    {
        float intensity = 1.0 - (min(leftDistance, rightDistance) / radius);
        color *= intensity;
    }
    else
    {
        color = vec3(0.0);
    }

    fragColor = vec4(color, 1.0);
}
