void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;
    fragColor = vec4(col, 1.0);
}

mat2 rot(float a)
{
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float circle(vec2 p, float r)
{
    return smoothstep(3./iResolution.y,0.,length(p)-r);
}

vec3 color(vec2 p, float r, float off)
{
    vec2 auv = vec2(atan(p.x,p.y), length(p));
    float time = iTime + off;
    float o = sin(time+p.x*3.*cos(auv.x*2.)*10.)*0.01;
    float f = cos(time - auv.x*2.);
    float s = r + cos(time+p.y)*o*r;

    return vec3(circle(p + o, s),
                circle(p - o * f, s),
                circle(p - o, s));
}

vec3 drawLines(vec2 p, float offset, float width)
{
    vec3 c = color(p, width, offset);
    c *= (1.0 - color(p, width * 0.99, offset));
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    uv /= 8.0;

    vec3 col = drawLines(uv, 0.0, 0.1);

    float iter = 40.0;
    for(float i=0.0;i<iter;i++)
    {
        uv *= rot(3.1415927/iter);
        uv *= 1.01;
        col += drawLines(uv, i/iter, 0.1);
    }

    col /= 3.0;

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;

    float fade = .45;
    float up = 1.5;
    float sway = 0.005;
    col += texture(iChannel1, (uv-(up/8.75+vec2(cos(iTime),sin(iTime))*sway))*up).bgr * fade;
    fragColor = vec4(col, 1.0);
}
