#define PI 3.14159
#define TAU 6.28318
#define SIDES 7.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    float a = atan(uv.x, uv.y)+3.14159;
    float side = TAU / SIDES;
    float cell = (abs(cos(iTime*0.3)*cos(iTime*0.3)) * SIDES);

    a = floor( ( cell+2.) - a/(side)) * floor(a/(side * cell));

    float c = length(uv);

    col = mix(col, vec3(1., 0.5, 0.), 1.-step(0.05, c));
    col = mix(col, vec3(1., 1., 0.), a * step(0.05, c));

    fragColor = vec4(col,1.0); 
}
