vec3 texelFetch2(vec2 uv,float lod){
    return pow(textureLod(iChannel0,uv,lod).xyz,vec3(2.2));
}

vec3 blur(vec2 uv, float radius){
    vec4 result = vec4(0.);
    for(float i=0.;i<10.;i++){
        float w = exp(-pow(log2(radius)-i,2.));
        result += vec4(texelFetch2(uv,i)*w,w);
    }
    return result.xyz/result.w;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    if(mod(iTime,6.)>4.) fragColor = vec4(texture(iChannel0,uv).xyz,1.0);
    else if(abs(uv.x+uv.y-1.)<.01) fragColor=vec4(0.,0.,0.,1.);
    else if(uv.x+uv.y>1.) fragColor = vec4(pow(blur(uv,100.),vec3(1./2.2))+texture(iChannel1,uv).xyz,1.0);
    else fragColor = vec4(texture(iChannel1,uv).xyz,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col = texture(iChannel0,fragCoord/iResolution.xy);

    fragColor = pow(col,vec4(4.));
}
