void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec2 scale = iResolution.xy / iResolution.y;

    uv *= scale;

    vec2 uvc = 2.* (uv - .5);

    float ang = .1 * atan(uvc.x, uvc.y);


    vec4 col = vec4(1.);

    float l = length(uvc);

    col = vec4(l * texture(iChannel0, vec2(ang)).r);

    float l2 = l * l;


    //use desmos to see what this does
    float f = 2. * (1. - l);
    float g = 2. * l;

    col *= clamp(f * g, 0., 1.);


    //color
    //col *= 2. * vec4(.2, .5, 1., 1.);
    col *= 2. * vec4(.6, .9, .1, 1.);


    //pupil
    col *= mix(0., 1., smoothstep(.0, .1, l2));

    //noise 1 for offset of radial distance ring noise
    float n0 = 2. * texture(iChannel0, .3 * vec2(ang)).r;

    //radial distance ring noise
    float n = texture(iChannel0, .05 * vec2(l2 + n0)).r;
    col *= mix(.3, 1., smoothstep(0., 1., n));

    fragColor = col;



}

float heaviside(float x, float a){
    x -= a;
    return ((x / abs(x)) + 1.) * .5;
}
