// Fork of "Hypnose" by Jespertheend. https://shadertoy.com/view/4sd3Wj
// 2023-03-21 10:05:33

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord.xy;
    vec2 center = iResolution.xy / 2.0;
    float dist = length(center - coord);

    // Modify the formula to create a different spiral pattern
    float circlesOut = cos(dist/4.0 - iTime*8.0) + cos(dist/5.0 - iTime*12.0);
    circlesOut *= 5.0;

    float circlesIn = cos(dist/3.0 + iTime*10.0) + cos(dist/6.0 + iTime*14.0);
    circlesIn *= 5.0;

    circlesIn = clamp(circlesIn, 0.0, 1.0);
    circlesOut = clamp(circlesOut, 0.0, 1.0);

    float edge = clamp(dist - 200.0, 0.0, 1.0);
    circlesOut *= edge;
    circlesIn *= 1.0 - edge;

    float c = circlesOut + circlesIn;
    fragColor = vec4(c, c, c, 0.0);
}
