#ifndef ALT
#define ALT 0 //set to 1 for an alternate, without checkerboards
#endif

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
  float t=iTime * 0.5;
  float s=5.0;
  float r=0.40;

  vec2 p=(fragCoord.xy / iResolution.xy) * 1.0 - 0.5;
  vec2 q=fragCoord.xy / iResolution.xy;

  p.y /=sin(p.x * 32.0 + t * s) * r;
  p.y +=cos(p.x * 39.0 + t * 2.5 * s) * r;
  #if ALT==0
  p.y *=atan(0.0, p.y);
  #endif

  #if ALT==1
  p.y *=fract(0.5)*5.0;
  #endif

  float d=length(q.y/p.y);
  float v=smoothstep(0.85, 1.0, 1.0 / d);

  fragColor=vec4(vec3(1.0) * v, 1.0);
}
