#define I iResolution
#define r( r ) mat2(cos(r), sin(r), -sin(r), cos(r) )

void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = ( V * 2. - I.xy ) / I.x,
    // fract( u - iTime * .2 ) depending on mods or map texture2D
         v = mod( u, 1.8 ) - .9;

    u += v;

    // Time varying pixel color

    float k = .2, i;

    for( ;i<20.;i++ ){
        u = abs( u ) - .1; // or not sub
        u.y = abs( u.y ) - .1 * k;
        u *= r( i/3.1415 + ( .6 - sin( iTime / i ) * .1 ) );
        u.x = sin( abs( u.x ) - .01 );
        k *= 1.1;
    }

    // Output to screen
    U = vec4( smoothstep( .01, abs( length( u ) ) - .1, .02 ) );
}
