void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0 - vec2(iResolution.xy))/iResolution.yy;

    float tunel_dist = 4.0 / sqrt(dot(uv, uv));

    float distance_fade = clamp(1.0 - tunel_dist/35.0, 0.0, 1.0);

    float angle = atan(uv.x, uv.y);

    tunel_dist += iTime*2.0;
    tunel_dist += round(angle*180.0 / 3.14 / 22.5);

    float col = round(fract(tunel_dist*0.5));

    col *= distance_fade;

    fragColor = vec4(col,col,col,1.0);
}
