mat2 rotate2D(float angle) {
    float c = sin(angle);
    float s = cos(angle);
    return mat2(c, -s, s, c);
}

void mainImage(out vec4 o, vec2 FC) {
    float i,e,g,v,u;
    vec3 p;

    o = vec4(0.0);

    for(i = 1.0, g = 0.0; i < 80.0; i++, g += e) {
        p = vec3((0.5 * iResolution.xy - FC.xy) / iResolution.y * g, g - 6.0);
        p.xz *= rotate2D(iTime * 0.2);

        e = v = 2.0;
        for(int j = 0; j < 12; j++) {
            if(j > 3) {
                e = min(e, length(p.xz + length(p) / u * 0.5) / v - 0.005);
                p.xz = abs(p.xz) - vec2(0.7);
            } else {
                p = abs(p) - vec3(0.9);
            }

            v /= u = dot(p, p);
            p /= u;
            p.y = 1.7 - p.y;
        }

        o.rgb += vec3(4.0, 2.0, 1.0) * 0.007 / exp(p.y / v);
    }

    o.a = 1.0;
}
