float HexDist(vec2 uv) {
    uv = abs(uv);

    float c = dot(uv, normalize(vec2(1,1.73)));
    c = max(c, uv.x);

    return c;
}
vec4 HexCoords(vec2 uv) {
    vec2 r = vec2(2, 4);
    vec2 h = r*.5;

    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv;
    if(length(a)<length(b))
       gv = a;
    else
       gv = b;

    float x = atan(gv.x, gv.y);
    float y =.5-HexDist(gv);

    vec2 id = uv-gv;
    return vec4(x, y, id.x,id.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.10*iResolution.xy)/iResolution.y;

    float t = iTime * 0.1;
    float r = sin(t * 1.0 + 1.0);
    float g = sin(t * 4.0 + 2.0);
    float b = sin(t * 1.0 + 3.0);
    vec3 col = vec3(r,g,b);

    uv*= 10.;


    vec4 hc = HexCoords(uv+100.) ;
    float c = smoothstep(.02,.8,hc.y*sin(hc.z*hc.w+iTime*2.));
    col += c;

    fragColor = vec4(col,1.0);
}
