void mainImage(out vec4 color, in vec2 coord)
{
    vec2 res = iResolution.xy;
    vec2 xy = ((coord - res / 2.0) * 2.0) / res.yy;
    vec3 ray = normalize(vec3(xy.x, xy.y + 0.8, 2.0));
    for (int i = 30; i < 100; i++) {
        vec3 p = ray * float(i) * 0.1 + vec3(0.0, -2.0, -6.0);
        float dist = pow(2.0 - length(p.xz), 2.0) + p.y * p.y;
        color += vec4(0.3, 0.2, 1.0, 0.0) * 0.02 / dist;
    }
    color = vec4(sqrt(color.rgb * 0.5), 1.0);
}
