void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    vec3 normals = vec3(uv,1);
    normals.z -= 3./sin(normals.x * 1000.) * .1;
     normals.z -= 3./sin(normals.y * 1000.) * .1;
     normals.z -= 3./cos(length(normals)* 10. - iTime * 10.) * .5;
    vec3 col;
    col += dot(normalize(vec3(100. * cos(iTime),100. * sin(iTime),10.)-vec3(uv,0)),normals);

    // Output to screen
    fragColor = vec4(col,1.0);
}
