vec3 blue = vec3(0.4453125, 0.890625, 0.890625);
vec3 yellow_bright = vec3(0.9296875, 0.8671875, 0.18359375);
vec3 yellow_dark = vec3(0.921875, 0.80078125, 0.);
vec3 orange = vec3(0.88671875, 0.625, 0.1171875);
vec3 pink = vec3(0.92578125, 0.46484375, 0.89453125);

#define PI acos( -1. )
#define GR 1.61803399

vec3 getColor(vec2 uv) {

    float step = .2;
    float feather = 0.01;
    float edge = step;

    float len = fract(length(uv));
    vec3 color = mix(blue, yellow_bright, smoothstep(edge, edge + feather, len));
    edge += step;
    color = mix(color, yellow_dark, smoothstep(edge, edge + feather, len));
    edge += step;
    color = mix(color, orange, smoothstep(edge, edge + feather, len));
    edge += step;
    color = mix(color, pink, smoothstep(edge, edge + feather, len));
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (2. * fragCoord.xy - iResolution.xy) / max(iResolution.x, iResolution.y);
    float an = iTime * PI * .2;
    float san = sin(an);
    float sqi = abs(san);

    float a = sqi * sin(iTime * 0.01);
    float b = sqi * -abs(sin(iTime * 0.05));
    vec2 t = vec2(-abs(san), san) * .25;

    for(float i = 1.; i < 4.; i += 1.) {

        float ai = a * i * PI;
        float bi = b * i * PI;
        mat2 m = mat2(sin(ai * uv.x), -sin(bi * uv.y), sin(bi * uv.x), sin(ai * uv.y));
        uv += t * m * i;

    }

    fragColor = vec4(getColor(uv), 1.0);

}
