/*
vec3 barycentric(vec2 a, vec2 b, vec2 c, vec2 p)
{
    float d = (b.y - c.y) * (a.x - c.x) + (c.x - b.x) * (a.y - c.y);
    float alpha = ((b.y - c.y) * (p.x - c.x)+(c.x - b.x) * (p.y - c.y)) / d;
    float beta = ((c.y - a.y) * (p.x - c.x) + (a.x - c.x) * (p.y - c.y)) / d;
    float gamma = 1.0 - alpha - beta;
    return vec3(alpha, beta, gamma);
}
*/
vec3 barycentric(vec2 p)
{
    float s = sqrt(3.0);
    return vec3((vec2(-1,+1) * p.x - p.y/s + 1.0)/2.0, p.y / s);
}

void mainImage( out vec4 c, in vec2 I )
{
    vec2 R = iResolution.xy;
    //I = 2.0 * I/R - 1.0;
    I = (2.0 * I - R) / R.y; // mind aspect ratio

    //vec3 t = barycentric(vec2(-1,0), vec2(+1,0), vec2(0,h), I*2.0*sqrt(3.0));
    vec3 b = barycentric(I*2.0*sqrt(3.0));

    ivec3 t = ivec3(floor(b)); // tile coordinates
    bool flip = t.x + t.y + t.z == 0; // triangle direction
    vec3 l = fract(b); // local coordinates
    if(!flip) l = 1.0 - l; // fix coordinates of every other triangles
    vec3 o = l.yzx - l.zxy; // orthogonal local coordinates
    c = vec4(o.x > 0.0 != o.y > 0.0 != o.z > 0.0); // checker pattern
    if(iMouse.z > 0.0) c = mix(c, vec4(flip), 0.75); // click to show underlying triangular grid

    c = sqrt(c); // gamma
}
