#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define light vec3(.5,.2,-1.)
float box( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float m2 (vec3 p, vec2 u) {
    float h = .5*abs(sin(iTime))*(.5-.5*cos(iTime*texture(iChannel0,u).x));
    if (u.x<0.||u.y<0.||u.x>1.||u.y>1.)
    return box(p-vec3(.5,.5,0),vec3(.5,.5,.1));
    return box(p-vec3(u,-h),vec3(.5/200.,.5/200.,.5/100.));
}
float map (vec3 p) {
    p.xy += .5;
    vec2 u = round(p.xy*300.)/300.;
    return min(min(m2(p,u),length(p-light)-.05),abs(p.z-.1)-.1);
}
vec3 normal (vec3 p) {
    return normalize(vec3(
        map(p+vec3(1e-3,0,0))-map(p-vec3(1e-3,0,0)),
        map(p+vec3(0,1e-3,0))-map(p-vec3(0,1e-3,0)),
        map(p+vec3(0,0,1e-3))-map(p-vec3(0,0,1e-3))
    ));
}
Main {
    vec3 p = vec3(0,0,-1.2);
    vec3 d = normalize(vec3(2.*(U-.5*R)/R.y,2));
    if (iMouse.z>0.) {

        p.yz *= ei(iMouse.y/R.y);
        d.yz *= ei(iMouse.y/R.y);
        p.xy *= ei(6.2*iMouse.x/R.x);
        d.xy *= ei(6.2*iMouse.x/R.x);
    } else {
        p.yz *= ei(.7);
        d.yz *= ei(.7);
        p.xy *= ei(iTime);
        d.xy *= ei(iTime);
    }
    for (float i = 0.; i < 200.; i++) {
        float m = map(p);
        p += .5*min(m,.03)*d;
    }

    Q = texture(iChannel1,normal(p));

    if (map(p)>.01) Q = vec4(.5);
    else {
    d = normalize(light-p);
    p += .01*d;
    for (float i = 0.; i < 50.; i++) {
        float m = map(p);
        p += .5*min(m,.03)*d;
    }
    Q *= exp(-abs(length(p-light)-.1));
    }
}
