#define PI 3.1415926535

float s_curve (float i, float mul, float p)
{
    float x_floor = floor(i * mul);
    float x = i * mul - x_floor;
    float x_1m = 1.0 - x;
    float x_p1 = pow(x, p);
    float x_p2 = pow(x_1m, p);
    float curve = x_p1 / (x_p1 + x_p2);
    return curve + x_floor;
}

vec2 rotate(vec2 uv, float th)
{
    return mat2(cos(th), sin(th),
               -sin(th), cos(th)) * uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * vec2(1.0, iResolution.y / iResolution.x);

    uv = fract(uv * 3.0);

    float speed = 0.07;
    float curve = s_curve(iTime, speed * 4.0, 10.0);

    float mul_x1 = cos(iTime * speed * PI * 2.0 + 0.35 * PI);
    mul_x1 *= mul_x1;
    float mul_x2 = 1.0 - mul_x1;
    float mul_x1_p = pow(mul_x1, 8.0);
    float mul_curve = mul_x1_p / (mul_x1_p + pow(mul_x2, 8.0));
    float mul_final = mix(1.0, sqrt(2.0), mul_curve);



    float rotation = curve * PI / 4.0;// + PI / 4.0;
    vec2 uv_rot = rotate((uv - 0.5) * mul_final, rotation) + 0.5;



    vec3 col = texture(iChannel0, uv_rot, 0.0).rgb;
    //col = vec3(uv, 0.0);

    // Output to screen
    fragColor = vec4(vec3(col), 1.0);
}
